CREATE OR REPLACE FUNCTION obter_desc_fatores_npt ( nr_seq_fator_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_fator_w	varchar(255);


BEGIN

if (ie_opcao_p = 'A') then

select	ds_fator
into STRICT	ds_fator_w
from	nut_fator_ativ
where	nr_sequencia = nr_seq_fator_p;

elsif (ie_opcao_p = 'S') then

select	ds_fator
into STRICT	ds_fator_w
from	nut_fator_stress
where	nr_sequencia = nr_seq_fator_p;

end if;


return	ds_fator_w;

end;
