CREATE OR REPLACE FUNCTION obter_desc_ficha_tecnica (nr_seq_ficha_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80);


BEGIN

if (nr_seq_ficha_p > 0) then
	select	substr(ds_substancia,1,80)
	into STRICT	ds_retorno_w
	from	medic_ficha_tecnica
	where	nr_sequencia	= nr_seq_ficha_p;
end if;

return ds_retorno_w;

end;
$body$
