SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_grace_ii ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*
Descrição 			ie_opcao_p

Risco no hospital		RH
Risco de 6 meses		R6
Risco de 1 ano			R1
Risco de 3 anos			R3
Risco de 1 ano MI		RMI


*/
ds_retorno_w varchar(255) := '';

QT_PONTUACAO_w				double precision;
QT_PONTUACAO_1_w			double precision;
QT_PONTUACAO_3_w			double precision;
QT_PONTUACAO_6_w			double precision;
QT_PONTUACAO_SCORE_6_w		double precision;
QT_PONTUACAO_MI_w			double precision;
IE_PONT_1_ANO_MAIOR_w		varchar(1);
IE_PONT_1_ANO_MI_MAIOR_w	varchar(1);
IE_PONT_3_ANO_MAIOR_w		varchar(1);
IE_PONT_6_MESES_MAIOR_w		varchar(1);
IE_PONT_HOSPITAL_MAIOR_w	varchar(1);



BEGIN

Select 	max(QT_PONTUACAO),
		max(QT_PONTUACAO_1),
		max(QT_PONTUACAO_3),
		max(QT_PONTUACAO_6),
		max(QT_PONTUACAO_SCORE_6),
		max(QT_PONTUACAO_MI),
		max(IE_PONT_1_ANO_MAIOR),
		max(IE_PONT_1_ANO_MI_MAIOR),
		max(IE_PONT_3_ANO_MAIOR),
		max(IE_PONT_6_MESES_MAIOR),
		max(IE_PONT_HOSPITAL_MAIOR)
into STRICT	QT_PONTUACAO_w,
		QT_PONTUACAO_1_w,
		QT_PONTUACAO_3_w,
		QT_PONTUACAO_6_w,
		QT_PONTUACAO_SCORE_6_w,
		QT_PONTUACAO_MI_w,
		IE_PONT_1_ANO_MAIOR_w,
		IE_PONT_1_ANO_MI_MAIOR_w,
		IE_PONT_3_ANO_MAIOR_w,
		IE_PONT_6_MESES_MAIOR_w,
		IE_PONT_HOSPITAL_MAIOR_w
from	escala_grace_ii
where	nr_sequencia = nr_sequencia_p;


if (ie_opcao_p = 'RH') then

	if (QT_PONTUACAO_w < 1) then

		ds_retorno_w := '0' || to_char(QT_PONTUACAO_w);

	elsif (coalesce(IE_PONT_HOSPITAL_MAIOR_w,'N') = 'S') then

		ds_retorno_w := '>' || to_char(QT_PONTUACAO_w);

	else

		ds_retorno_w := to_char(QT_PONTUACAO_w);

	end if;


elsif (ie_opcao_p = 'R6') then

	if (QT_PONTUACAO_1_w < QT_PONTUACAO_6_w) then

		if (QT_PONTUACAO_1_w < 1) then

			ds_retorno_w := '0' || to_char(QT_PONTUACAO_1_w) || ' - ';

		else

			ds_retorno_w := to_char(QT_PONTUACAO_1_w) || ' - ';

		end if;

		if (QT_PONTUACAO_6_w < 1) then

			ds_retorno_w := ds_retorno_w || '0' || to_char(QT_PONTUACAO_6_w);

		else

			ds_retorno_w := ds_retorno_w || to_char(QT_PONTUACAO_6_w);

		end if;

		ds_retorno_w := ds_retorno_w || ' (' || to_char(Arredondondar_Grace_II(QT_PONTUACAO_SCORE_6_w, 1)) || ')';


	elsif (coalesce(IE_PONT_6_MESES_MAIOR_w, 'N') = 'S') then

		ds_retorno_w := '>' || to_char(QT_PONTUACAO_6_w) || ' (' || to_char(Arredondondar_Grace_II(QT_PONTUACAO_SCORE_6_w, 1)) || ')';

	else

		if (QT_PONTUACAO_6_w < 1) then

			ds_retorno_w := '0' || to_char(QT_PONTUACAO_6_w) || ' (' || to_char(Arredondondar_Grace_II(QT_PONTUACAO_SCORE_6_w, 1)) || ')';

		else

			ds_retorno_w := to_char(QT_PONTUACAO_6_w) || ' (' || to_char(Arredondondar_Grace_II(QT_PONTUACAO_SCORE_6_w, 1)) || ')';

		end if;

	end if;


elsif (ie_opcao_p = 'R1') then

	if (QT_PONTUACAO_1_w < QT_PONTUACAO_6_w) then

		if (QT_PONTUACAO_1_w < 1) then

			ds_retorno_w := '0' || to_char(QT_PONTUACAO_1_w) || ' - ';

		else

			ds_retorno_w := to_char(QT_PONTUACAO_1_w) || ' - ';

		end if;

		if (QT_PONTUACAO_6_w < 1) then

			ds_retorno_w := ds_retorno_w || '0' || to_char(QT_PONTUACAO_6_w);

		else

			ds_retorno_w := ds_retorno_w || to_char(QT_PONTUACAO_6_w);

		end if;



	elsif (coalesce(IE_PONT_1_ANO_MAIOR_w, 'N') = 'S') then

		ds_retorno_w := '>' || to_char(QT_PONTUACAO_1_w);

	else

		if (QT_PONTUACAO_1_w < 1) then

			ds_retorno_w := '0' || to_char(QT_PONTUACAO_1_w);

		else

			ds_retorno_w := to_char(QT_PONTUACAO_1_w);

		end if;

	end if;

elsif (ie_opcao_p = 'R3') then

	if (coalesce(IE_PONT_3_ANO_MAIOR_w, 'N') = 'S') then

		ds_retorno_w := '>' || to_char(QT_PONTUACAO_3_w);

	else

		if (QT_PONTUACAO_3_w < 1) then

			ds_retorno_w := '0' || to_char(QT_PONTUACAO_3_w);

		else

			ds_retorno_w := to_char(QT_PONTUACAO_3_w);

		end if;

	end if;

elsif (ie_opcao_p = 'RMI') then

	if (QT_PONTUACAO_1_w < QT_PONTUACAO_6_w) then

		QT_PONTUACAO_1_w := Arredondondar_Grace_II(QT_PONTUACAO_1_w + ((QT_PONTUACAO_6_w - QT_PONTUACAO_1_w) / 2), 1);

	end if;

	if (QT_PONTUACAO_1_w >= QT_PONTUACAO_MI_w) then

		QT_PONTUACAO_MI_w := QT_PONTUACAO_1_w;

		ds_retorno_w := '>' || to_char(QT_PONTUACAO_MI_w);

	elsif (coalesce(IE_PONT_1_ANO_MI_MAIOR_w, 'N') = 'S') then

		ds_retorno_w := '>' || to_char(QT_PONTUACAO_MI_w);

	else

		if (QT_PONTUACAO_MI_w < 1) then

			ds_retorno_w := '0' || to_char(QT_PONTUACAO_MI_w);


		else

			ds_retorno_w := to_char(QT_PONTUACAO_MI_w);

		end if;

	end if;

end if;


return 	ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
