SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_grupo_adicional (ds_codigo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_codigo_w		varchar(4000);
cd_posicao_w		varchar(10);
ds_grupo_w		varchar(4000);
nr_seq_grupo_w		bigint;


BEGIN

ds_codigo_w	:= ds_codigo_p;

if (ds_codigo_p IS NOT NULL AND ds_codigo_p::text <> '') then

	cd_posicao_w	:=position(',' in ds_codigo_w);

	while(cd_posicao_w <> '0') loop
		nr_seq_grupo_w	:= somente_numero(substr(ds_codigo_w,1,cd_posicao_w-1));

		select	ds_grupo
		into STRICT	ds_grupo_w
		from	grupo_usuario
		where	nr_sequencia	= nr_seq_grupo_w;

		ds_retorno_w	:= ds_grupo_w ||', '||ds_retorno_w;
		ds_codigo_w	:= substr(ds_codigo_w,(cd_posicao_w)::numeric  + 1,length(ds_codigo_w));

		cd_posicao_w	:= position(',' in ds_codigo_w);
	end loop;

end if;

RETURN ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
