SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_grupo_agen ( nr_seq_grupo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);

/*
ie_opcao_p = 'D' = versão Delphi
	   'J' = versão JAVA
*/
BEGIN

if (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then

	if (ie_opcao_p = 'D') then

		select 	substr(ds_grupo,1,80)
		into STRICT	ds_retorno_w
		from 	agenda_int_grupo
		where 	nr_sequencia = nr_seq_grupo_p;

	elsif (ie_opcao_p = 'J') then

		select 	substr(ds_grupo,1,80)
		into STRICT	ds_retorno_w
		from   	agenda_int_grupo
		where 	nr_sequencia = nr_seq_grupo_p
		and  	ie_situacao = 'A'
		and    	ie_tipo_Agendamento <> 'C';

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
