CREATE OR REPLACE FUNCTION obter_desc_grupo_item ( nr_seq_grupo_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN

if (coalesce(nr_seq_grupo_p,0) > 0) then

	select	max(ds_grupo)
	into STRICT	ds_retorno_w
	from	sl_grupo_check_list
	where	nr_sequencia = nr_seq_grupo_p;

elsif (coalesce(nr_seq_item_p,0) > 0) then

	select	max(ds_item)
	into STRICT	ds_retorno_w
	from	sl_item_check_list
	where	nr_sequencia = nr_seq_item_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
