CREATE OR REPLACE FUNCTION obter_desc_grupo_perfil (ds_codigo_p text) RETURNS varchar AS $body$
DECLARE


lista_grupo_perfil_w		lista_varchar_pck.tabela_varchar;

ds_retorno_w		varchar(4000);
ds_codigo_w		varchar(4000);
ds_grupo_w		varchar(4000);


BEGIN

ds_codigo_w	:= ds_codigo_p;

if (ds_codigo_p IS NOT NULL AND ds_codigo_p::text <> '') then
	lista_grupo_perfil_w := obter_lista_string2(ds_codigo_w, ',');

	for	i in lista_grupo_perfil_w.first..lista_grupo_perfil_w.last loop
			begin
			select	obter_desc_expressao(cd_exp_grupo, ds_grupo)
			into STRICT	ds_grupo_w
			from	grupo_perfil
			where	nr_sequencia	= (lista_grupo_perfil_w(i))::numeric;

			ds_retorno_w	:= ds_grupo_w ||', '||ds_retorno_w;
			exception
			when others then
				ds_retorno_w := ds_retorno_w;
			end;
	end loop;

end if;

RETURN ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
