CREATE OR REPLACE FUNCTION obter_desc_grupo_trab ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	substr(obter_desc_expressao(cd_exp_grupo_trabalho,ds_grupo_trabalho),1,255)
	into STRICT	ds_retorno_w
	from	man_grupo_trabalho
	where	nr_sequencia	= nr_sequencia_p;
	end;
end if;

RETURN ds_retorno_w;

END;
$body$
