CREATE OR REPLACE FUNCTION obter_desc_hem_segmento_syntax (cd_segmento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80);

/*
ie_opcao_p
N - Pega pela sequência
C - Pega pelo código informado
CS - Código do segmento passando a sequência
*/
BEGIN

if (cd_segmento_p IS NOT NULL AND cd_segmento_p::text <> '') then

	if (ie_opcao_p = 'C') then
		select max(ds_segmento)
		into STRICT ds_retorno_w
		from hem_segmento_syntax
		where cd_segmento = cd_segmento_p;
	elsif (ie_opcao_p = 'CS') then
		select max(cd_segmento)
		into STRICT ds_retorno_w
		from hem_segmento_syntax
		where nr_sequencia = (cd_segmento_p)::numeric;
	else
		select max(ds_segmento)
		into STRICT ds_retorno_w
		from hem_segmento_syntax
		where nr_sequencia = (cd_segmento_p)::numeric;
	end if;
end if;


return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
