CREATE OR REPLACE FUNCTION obter_desc_ie_tipo_tratamento ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(50);


BEGIN
IF (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') THEN
	BEGIN

	SELECT	CASE WHEN ie_tipo_tratamento='P' THEN wheb_mensagem_pck.get_texto(799384) || '-' || wheb_mensagem_pck.get_texto(798641)  ELSE wheb_mensagem_pck.get_texto(798641) END
	INTO STRICT	ds_retorno_w
	FROM	CA_MANUTENCAO_TRATAMENTO
	WHERE	nr_sequencia = nr_sequencia_p;
	END;
END IF;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
