CREATE OR REPLACE FUNCTION obter_desc_intervalo_hemo (nr_seq_presc_procedimento_p bigint, nr_seq_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(40);




BEGIN

select	substr(a.ds_intervalo,1,40)
into STRICT    ie_retorno_w
from	intervalo_prescricao a,
	prescr_procedimento b
where   b.nr_sequencia = nr_seq_presc_procedimento_p
and	b.nr_prescricao = nr_seq_prescricao_p
and	a.cd_intervalo = b.cd_intervalo;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
