SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_item_assoc_proc ( nr_sequencia_p bigint, nr_prescricao_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_componentes_w	varchar(2000);
ds_material_w		varchar(80);

c01 CURSOR FOR
SELECT	substr(b.ds_material,1,80) ds_material
from	material b,
	prescr_material a
where	a.nr_prescricao		= nr_prescricao_p
and	a.nr_sequencia_solucao	= nr_sequencia_p
and	a.cd_material		= b.cd_material
and	coalesce(a.dt_suspensao::text, '') = ''
order by
	ds_material;


BEGIN

if (ie_tipo_p = 'M') then
	select	substr(max(b.ds_material),1,255)
	into STRICT	ds_retorno_w
	from	prescr_material a,
		material b
	where	a.cd_material	= b.cd_material
	and	a.nr_prescricao	= nr_prescricao_p
	and	a.nr_sequencia	= nr_sequencia_p;
elsif (ie_tipo_p = 'S') then

	open c01;
	loop
	fetch c01 into	ds_material_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ds_material_w IS NOT NULL AND ds_material_w::text <> '') and (coalesce(length(ds_componentes_w),0) < 1900) then
			ds_componentes_w := ds_componentes_w || chr(13) ||ds_material_w;
		end if;
		end;
	end loop;
	close c01;

	select	max(coalesce(ds_solucao,ds_componentes_w))
	into STRICT	ds_retorno_w
	from	prescr_solucao
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_sequencia_p;
end if;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309033, 'DS_RETORNO_W='||ds_retorno_w); --'Gerado pelo dispositivo do item ' || ds_retorno_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_desc_item_assoc_proc ( nr_sequencia_p bigint, nr_prescricao_p bigint, ie_tipo_p text) FROM PUBLIC;

