CREATE OR REPLACE FUNCTION obter_desc_item_escala (ie_escala_p bigint, nr_seq_item_escala_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(255);

BEGIN
 
if (ie_escala_p IS NOT NULL AND ie_escala_p::text <> '') and (nr_seq_item_escala_p IS NOT NULL AND nr_seq_item_escala_p::text <> '') then 
	begin 
	select	substr(obter_desc_expressao(a.cd_exp_desc_item),1,254) 
	into STRICT	ds_retorno_w 
	from	pe_escala_item a, 
		pe_escala b 
	where	a.nr_seq_escala = b.nr_sequencia 
	and	b.ie_escala = ie_escala_p 
	and 	a.nr_sequencia = nr_seq_item_escala_p;
	end;
end if;
return	ds_retorno_w;
 
end;
$body$
