CREATE OR REPLACE FUNCTION obter_desc_item_manchester ( nr_seq_discriminador_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);
nr_seq_item_w bigint;


BEGIN

	if (coalesce(nr_seq_discriminador_p, 0) > 0) then

		select max(NR_SEQ_ITEM)
		into STRICT nr_seq_item_w
		from MANCHESTER_FLUXOGRAMA_ITEM
		where nr_sequencia = nr_seq_discriminador_p;

		if (coalesce(nr_seq_item_w, 0) > 0) then
			select substr(obter_desc_manchester_item(nr_seq_item_w),1,255)
			into STRICT ds_retorno_w
			;
		end if;
	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
