CREATE OR REPLACE FUNCTION obter_desc_item_perfil_oft ( nr_sequencia_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE



ds_instituicao_w	varchar(40);
cd_perfil_w       perfil.cd_perfil%type;


BEGIN
cd_perfil_w := cd_perfil_p;
if (coalesce(cd_perfil_w::text, '') = '') then
   cd_perfil_w := wheb_usuario_pck.get_cd_perfil;
end if;

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then

	select	substr(max(ds_instituicao),1,40)
	into STRICT	ds_instituicao_w
	from 	perfil_item_oftalmologia
	where 	nr_seq_item	= nr_sequencia_p
	and 	cd_perfil 	= cd_perfil_p;
	
	if (coalesce(ds_instituicao_w::text, '') = '') then
		select	substr(max(obter_desc_expressao(cd_exp_desc_item,ds_item)),1,40)
		into STRICT	ds_instituicao_w
		from	oftalmologia_item
		where	nr_sequencia = nr_sequencia_p;
	end if;	
end if;


return	ds_instituicao_w;

end;
$body$
LANGUAGE PLPGSQL
