CREATE OR REPLACE FUNCTION obter_desc_item_processo_adep (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_item_w	varchar(100);


BEGIN 
 
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then 
 
	select	substr(obter_dados_horario(nr_seq_horario_p,'DI'),1,100) 
	into STRICT	ds_item_w 
	;
 
elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then 
 
	select	substr(obter_desc_material_prescr(nr_prescricao_p,nr_seq_material_p),1,100) 
	into STRICT	ds_item_w 
	;
 
end if;
 
return ds_item_w;
 
end;
$body$
