CREATE OR REPLACE FUNCTION obter_desc_item_prontuario ( NR_SEQ_ITEM_PRONT_P bigint, ie_html_p text default 'N') RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(255);


BEGIN


if (NR_SEQ_ITEM_PRONT_P IS NOT NULL AND NR_SEQ_ITEM_PRONT_P::text <> '') then

	if (ie_html_p = 'S') then

	Select 	max(coalesce(ds_item_instituicao,obter_Desc_expressao(CD_EXP_DESC_ITEM_HTML,obter_Desc_expressao(CD_EXP_DESC_ITEM,ds_item))))
	into STRICT	ds_retorno_w
	from	prontuario_item
	where	nr_sequencia = NR_SEQ_ITEM_PRONT_P;

	else

	Select 	max(coalesce(ds_item_instituicao,obter_Desc_expressao(CD_EXP_DESC_ITEM,ds_item)))
	into STRICT	ds_retorno_w
	from	prontuario_item
	where	nr_sequencia = NR_SEQ_ITEM_PRONT_P;

	end if;


end if;

return ds_retorno_w;

end;
$body$
