SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_itens_processo_adep ( nr_seq_processo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_itens_w		varchar(4000) := '';
ds_item_w		varchar(255);

C01 CURSOR FOR
	SELECT	distinct(substr(obter_dados_material(cd_material, 'DR'),1,30) || '| ')
	from	prescr_mat_hor
	where	nr_seq_processo = nr_seq_processo_p
	order by 1;


BEGIN

if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then
	if (ie_opcao_p = 'MDR') then
		open C01;
		loop
		fetch C01 into
			ds_item_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ds_itens_w	:= substr(ds_itens_w || ds_item_w,1,4000);
			end;
		end loop;
		close C01;
	end if;
end if;


return	ds_itens_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
