CREATE OR REPLACE FUNCTION obter_desc_justificativa (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_justificativa_w   varchar(180);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	substr(ds_justificativa,1,180)
	into STRICT	ds_justificativa_w
	from	justif_conferencia_item
	where 	nr_sequencia = nr_sequencia_p;
end if;

return	ds_justificativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
