CREATE OR REPLACE FUNCTION obter_desc_kit_barras ( nr_prescricao_p bigint, cd_material_p bigint, nr_seq_lote_fornec_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_kit_estoque_w	varchar(255);
ds_kit_material_w	varchar(255);
nr_sequencia_w		bigint;
cd_kit_material_w	bigint;
nr_seq_kit_estoque_w	bigint;
ds_retorno_w		varchar(255) := null;


BEGIN
nr_sequencia_w	:= coalesce(nr_sequencia_p,0);

if (nr_sequencia_w = 0) then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	prescr_material
	where	nr_prescricao			= nr_prescricao_p
	and	cd_material 			= cd_material_p
	and	coalesce(nr_seq_lote_fornec,0)	= coalesce(nr_seq_lote_fornec_p,coalesce(nr_seq_lote_fornec,0))
	and	cd_motivo_baixa			= 0
	and	coalesce(dt_baixa::text, '') = '';
end if;

if (nr_sequencia_w > 0) then
	select	cd_kit_material,
		nr_seq_kit_estoque,
		substr(cd_kit_material || ' - '||obter_desc_kit(cd_kit_material),1,200),
		substr(nr_seq_kit_estoque || ' - '||obter_desc_kit_estoque(nr_seq_kit_estoque),1,200)
	into STRICT	cd_kit_material_w,
		nr_seq_kit_estoque_w,
		ds_kit_material_w,
		ds_kit_estoque_w
	from	prescr_material
	where	nr_sequencia = nr_sequencia_w
	and	nr_prescricao= nr_prescricao_p;
end if;

if (cd_kit_material_w IS NOT NULL AND cd_kit_material_w::text <> '') then
	ds_retorno_w := ds_kit_material_w;
elsif (nr_seq_kit_estoque_w IS NOT NULL AND nr_seq_kit_estoque_w::text <> '') then
	ds_retorno_w := ds_kit_estoque_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
