CREATE OR REPLACE FUNCTION obter_desc_leito_unid_compl (nr_seq_unidade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_unidade_basica_w	varchar(255);
ds_unidade_compl_w	varchar(255);


BEGIN
select	max(cd_unidade_basica),
        	max(cd_unidade_compl)
into STRICT	ds_unidade_basica_w,
	ds_unidade_compl_w
from	unidade_atendimento
where	nr_seq_interno	= nr_seq_unidade_p
and     	ie_status_unidade <> 'L';

if ((trim(both ds_unidade_compl_w) IS NOT NULL AND (trim(both ds_unidade_compl_w))::text <> '')) then
    	return ds_unidade_basica_w || ' - ' || ds_unidade_compl_w;
else
    	return ds_unidade_basica_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
