SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_local_qt (nr_sequencia_p bigint, ie_curta_longa_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(70);
ds_abrev_w		varchar(7);
ds_longa_w		varchar(60);
ds_cor_w			varchar(15);

-- ie_curta_longa_p  'C' Descrição curta - 'L' Descrição longa.
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ds_abrev,
		ds_local,
		ds_cor
	into STRICT	ds_abrev_w,
		ds_longa_w,
		ds_cor_w
	from	qt_local
	where	nr_sequencia = nr_sequencia_p;
end if;

if (ie_curta_longa_p = 'C') then
	ds_resultado_w	:= ds_abrev_w;
elsif (ie_curta_longa_p = 'R') then
	ds_resultado_w	:= ds_cor_w;
else
	ds_resultado_w	:= ds_longa_w;
end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
