CREATE OR REPLACE FUNCTION obter_desc_local_refeicao ( nr_seq_local_ref_p bigint) RETURNS varchar AS $body$
DECLARE


ds_local_ref_w	varchar(90)	:= '';


BEGIN

if (nr_seq_local_ref_p IS NOT NULL AND nr_seq_local_ref_p::text <> '') then
	begin

	select	ds_local_ref
