CREATE OR REPLACE FUNCTION obter_desc_local_senha (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(255);
ds_local_w	varchar(255);

BEGIN

if (nr_sequencia_p > 0) then
	begin

	select	max(ds_local)
	into STRICT	ds_local_w
	from	maquina_local_senha
	where	nr_sequencia = nr_sequencia_p
	and	((coalesce(ie_situacao,'A') = 'A') OR (nr_sequencia = nr_sequencia_p));

	end;
end if;

