SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_loc_material ( cd_material_p bigint, cd_local_estoque_p bigint, tipo_descricao_p text) RETURNS varchar AS $body$
DECLARE

/*
tipo de dado
'DS' - retorna o campo ds_localizacao do cadastro do material
'ES' - retorna a descricao do campo nr_seq_estrut_mat, caso estiver nulo, retorna descricao do campo nr_seq_local_w
*/
ds_localizacao_w		varchar(100)	:=	'X';
ds_localizacao_ww		varchar(100);
nr_seq_estrut_mat_w	localizacao_estoque_local.nr_seq_estrut_mat%type;
nr_seq_local_w		localizacao_estoque_local.nr_seq_local%type;


BEGIN

select	max(nr_seq_estrut_mat),
	max(nr_seq_local),
	substr(coalesce(max(ds_localizacao),'X'), 1, 100)
into STRICT	nr_seq_estrut_mat_w,
	nr_seq_local_w,
	ds_localizacao_ww
from	localizacao_estoque_local
where	cd_material		= cd_material_p
and	cd_local_estoque	= cd_local_estoque_p;

if (tipo_descricao_p = 'DS') then
	begin
	if (ds_localizacao_ww = 'X') then
		begin
		select	substr(coalesce(max(ds_localizacao),'X'), 1, 100)
		into STRICT	ds_localizacao_ww
		from 	padrao_estoque_local b,
			material a
		where	a.cd_material_estoque 	= b.cd_material
		and	a.cd_material		= cd_material_p
		and	b.cd_local_estoque	= cd_local_estoque_p;
		end;
	end if;
	ds_localizacao_w := ds_localizacao_ww;
	end;
elsif (tipo_descricao_p = 'ES') then
	begin
	if (nr_seq_estrut_mat_w IS NOT NULL AND nr_seq_estrut_mat_w::text <> '') then
		ds_localizacao_w	:=	substr(coalesce(obter_desc_estrut_loc_material(nr_seq_estrut_mat_w),'X'),1,100);
	elsif (nr_seq_local_w IS NOT NULL AND nr_seq_local_w::text <> '') then
		begin
		select	substr(coalesce(ds_local,'X'),1,100)
		into STRICT	ds_localizacao_w
		from	material_local
		where	nr_sequencia = nr_seq_local_w;
		exception
		when others then
			ds_localizacao_w	:=	'X';
		end;
	end if;

	if (ds_localizacao_w = 'X') then
		begin
		select	max(nr_seq_estrut_mat)
		into STRICT	nr_seq_estrut_mat_w
		from 	padrao_estoque_local b,
			material a
		where	a.cd_material_estoque 	= b.cd_material
		and	a.cd_material		= cd_material_p
		and	b.cd_local_estoque	= cd_local_estoque_p;

		if (nr_seq_estrut_mat_w IS NOT NULL AND nr_seq_estrut_mat_w::text <> '') then
			ds_localizacao_w	:=	substr(coalesce(obter_desc_estrut_loc_material(nr_seq_estrut_mat_w),'X'),1,100);
		end if;
		if (ds_localizacao_w = 'X') then
			ds_localizacao_w	:= substr(obter_dados_material(cd_material_p, 'DL'),1,100);
		end if;
		end;
	end if;
	end;
end if;

if (ds_localizacao_w = 'X') then
	ds_localizacao_w := '';
end if;

return ds_localizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
