CREATE OR REPLACE FUNCTION obter_desc_lote_lp ( nr_lote_producao_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_todos_lote_fornec_w	varchar(255);
ds_lote_fornec_w	varchar(20);
				
C01 CURSOR FOR 
	SELECT	distinct Obter_dados_lote_fornec(nr_seq_lote_fornec,'D') 
	from	lp_item_util 
	where	nr_lote_producao	=	nr_lote_producao_p 
	and	cd_material		=	cd_material_p;
				

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	ds_lote_fornec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (coalesce(ds_todos_lote_fornec_w::text, '') = '') then 
		ds_todos_lote_fornec_w	:= ds_lote_fornec_w;
	else	 
		ds_todos_lote_fornec_w	:= ds_todos_lote_fornec_w || ', ' || ds_lote_fornec_w;
	end if;
	end;
end loop;
close C01;
 
return	ds_todos_lote_fornec_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
