CREATE OR REPLACE FUNCTION obter_desc_macro_ci_colab (ie_evento_p text default '') RETURNS varchar AS $body$
DECLARE


ds_mensagem_w				varchar(2000);

/* macros
NOME
SETOR	(Somente desligamento)
CONSELHO
CODIGOPROF
VINCULOMEDICO
DATAADMISSAO
PADRAORECEBIMENTO
ENTIDADEJURIDICA
CARGO
OBSPF
CATEGORIA
ESPECIALIDADE
CBO
Setor Atual
Novo Setor
*/
BEGIN
ds_mensagem_w := substr(
		 '@NOME' || chr(13) ||chr(10) ||
		 wheb_mensagem_pck.get_texto(301096) || chr(13) ||chr(10) ||
		 '@CONSELHO' || chr(13) ||chr(10) ||
		 '@CODIGOPROF' || chr(13) ||chr(10) ||
		 '@VINCULOMEDICO' || chr(13) ||chr(10) ||
		 '@DATAADMISSAO' || chr(13) ||chr(10) ||
		 '@PADRAORECEBIMENTO' || chr(13) ||chr(10) ||
		 '@ENTIDADEJURIDICA' || chr(13) ||chr(10) ||
		 '@CARGO' || chr(13) ||chr(10) ||
		 '@OBSPF' || chr(13) ||chr(10) ||
		 '@CATEGORIA' || chr(13) ||chr(10) ||
		 '@ESPECIALIDADE' || chr(13) ||chr(10) ||
		wheb_mensagem_pck.get_texto(301097),1,255);
if (ie_evento_p = 'EA') then
	begin
	ds_mensagem_w := substr(ds_mensagem_w	|| chr(13) ||chr(10) || wheb_mensagem_pck.get_texto(301098)
						|| chr(13) ||chr(10) || wheb_mensagem_pck.get_texto(301099),1,2000);

	end;
elsif (ie_evento_p = 'TS') then
	begin
	ds_mensagem_w := substr(ds_mensagem_w 	|| chr(13) || chr(10) || '@ATUAL_SETOR'
						|| chr(13) || chr(10) || wheb_mensagem_pck.get_texto(301100),1,2000);

	end;
end if;

return ds_mensagem_w;

end;
