SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_man_equipamento ( nr_sequencia_p bigint, ie_localizacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
ds_localizacao_w		varchar(100);
nr_seq_local_w		bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ds_equipamento,
		nr_seq_local
	into STRICT	ds_retorno_w,
		nr_seq_local_w
	from	man_equipamento
	where	nr_sequencia	= nr_sequencia_p;

	if (ie_localizacao_p = 'S') then
		select	ds_localizacao
		into STRICT	ds_localizacao_w
		from	man_localizacao
		where	nr_sequencia	= nr_seq_local_w;

		ds_retorno_w	:= substr(ds_retorno_w ||' - '||ds_localizacao_w,1,100);
	end if;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
