CREATE OR REPLACE FUNCTION obter_desc_marca_oc (nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80) := '';


BEGIN
if (nr_ordem_compra_p IS NOT NULL AND nr_ordem_compra_p::text <> '') and (nr_item_oci_p IS NOT NULL AND nr_item_oci_p::text <> '') then
	select	substr(obter_desc_marca(nr_seq_marca),1,255)
	into STRICT	ds_retorno_w
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p;
end if;

return ds_retorno_w;

end;
