CREATE OR REPLACE FUNCTION obter_desc_material_dcb ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN

select	substr(coalesce(max(a.ds_dcb),obter_desc_material(cd_material_p)),1,255)
into STRICT	ds_retorno_w
from	material c,
	dcb_medic_controlado a,
	medic_ficha_tecnica d
where	c.nr_seq_ficha_tecnica	= d.nr_sequencia
and	a.nr_sequencia 		= d.nr_seq_dcb
and	c.cd_material 		= cd_material_p;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
