CREATE OR REPLACE FUNCTION obter_desc_material_marca ( nr_seq_marca_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) := '';


BEGIN
if (nr_seq_marca_p IS NOT NULL AND nr_seq_marca_p::text <> '') and (cd_material_p > 0) then
	select	max(substr(CASE WHEN b.cd_referencia='' THEN a.ds_marca  ELSE a.ds_marca || '(' || b.cd_referencia || ')' END ,1,255))
	into STRICT	ds_retorno_w
	from	marca a,
		material_marca b
	where	a.nr_sequencia = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_marca_p
	and	b.cd_material = cd_material_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
