CREATE OR REPLACE FUNCTION obter_desc_material_simpro (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_simpro_w		varchar(100);


BEGIN

select max(ds_produto)
into STRICT	ds_simpro_w
from	simpro_cadastro b,
	material_simpro a
where	a.cd_material	= cd_material_p
and	a.cd_simpro	= b.cd_simpro;

return ds_simpro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
