CREATE OR REPLACE FUNCTION obter_desc_matpaci_convenio (nr_seq_matpaci_p bigint) RETURNS varchar AS $body$
DECLARE


ds_material_w	varchar(255);


BEGIN

select	substr(coalesce(c.ds_material, b.ds_material),1,255)
into STRICT	ds_material_w
FROM material b, material_atend_paciente a
LEFT OUTER JOIN mat_atend_pac_convenio c ON (a.nr_sequencia = c.nr_seq_material)
WHERE a.cd_material		= b.cd_material and a.nr_sequencia		= nr_seq_matpaci_p;

return	ds_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
