CREATE OR REPLACE FUNCTION obter_desc_mat_autor_qt ( nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '')then

	select 	max(substr(obter_desc_material(a.cd_material),1,255))
	into STRICT 	ds_retorno_w
	from	material_autorizado a
	where	a.nr_prescricao = nr_prescricao_p
	and 	a.nr_seq_prescricao = nr_seq_material_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
