CREATE OR REPLACE FUNCTION obter_desc_mat_convenio (cd_material_convenio_p text, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_material_w	varchar(255);


BEGIN

select	substr(max(coalesce(c.ds_material_convenio, b.ds_material)),1,255)
into STRICT	ds_material_w
from	material b,
	conversao_material_convenio c
where	b.cd_material		= c.cd_material
and	c.cd_material_convenio	= cd_material_convenio_p
and	c.cd_convenio		= cd_convenio_p;

return	ds_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
