CREATE OR REPLACE FUNCTION obter_desc_mat_exame_lab ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(80);
ie_situacao_w			varchar(1);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select  max(ds_material_exame), max(ie_situacao)
	into STRICT	ds_retorno_w, ie_situacao_w
	from 	material_exame_lab
	where   nr_sequencia = nr_sequencia_p;

	if (coalesce(ie_situacao_w,'A') = 'I') then
		ds_retorno_w := ds_retorno_w || '(' || obter_desc_expressao(319239) || ')';
	end if;

end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
