CREATE OR REPLACE FUNCTION obter_desc_mat_exec_prescr (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_desc_reduzida_w	varchar(1);
ds_retorno_w		varchar(255);


BEGIN

ie_desc_reduzida_w := obter_param_usuario(24, 189, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_desc_reduzida_w);

if (coalesce(ie_desc_reduzida_w,'N') = 'N') then

	select 	substr(ds_material,1,255)
	into STRICT	ds_retorno_w
	from 	material
	where 	cd_material = cd_material_p;

else

	select 	substr(DS_REDUZIDA,1,255)
	into STRICT	ds_retorno_w
	from 	material
	where 	cd_material = cd_material_p;

end if;

return	ds_retorno_w;

end;
$body$
