CREATE OR REPLACE FUNCTION obter_desc_mat_generico (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

select	substr(b.ds_material,1,255)
into STRICT	ds_retorno_w
from	material a,
		estrutura_material_v b
where	a.cd_material = cd_material_p
and		b.cd_material = coalesce(a.cd_material_generico,a.cd_material);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
