CREATE OR REPLACE FUNCTION obter_desc_mat_lote_prescr ( nr_seq_lote_p bigint, cd_material_p bigint, ie_localizacao_p text, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(128);
ds_material_w		varchar(100);
ie_tipo_fonte_w		varchar(003);
ds_local_w		varchar(80);
cd_material_w		integer;
ie_urgencia_w		varchar(01);
cd_local_w		integer;
nr_prescricao_w		bigint;


BEGIN

/* obter prescrição do lote*/

select	max(nr_prescricao)
into STRICT	nr_prescricao_w
from	ap_lote
where	nr_sequencia = nr_seq_lote_p;

/* local de estoque do setor da prescrição*/

if (ie_localizacao_p = 'S') and (coalesce(cd_local_estoque_p,0) = 0) then
	select	max(b.cd_local_estoque)
	into STRICT	cd_local_w
	from	setor_atendimento b,
		prescr_medica a
	where	a.cd_setor_atendimento	= b.cd_setor_atendimento
	and	nr_prescricao	= nr_prescricao_w;
end if;

cd_local_w 	:= coalesce(cd_local_w, cd_local_estoque_p);
cd_material_w	:= cd_material_p;

/* obter se o item é urgente */

select	coalesce(max(ie_urgente),'N')
into STRICT	ie_urgencia_w
from	ap_lote_item
where	nr_seq_lote	= nr_seq_lote_p
and	cd_material	= cd_material_w;

/* descrição do material e tipo de fonte no relatorio*/

select	substr(max(ds_material),1,100),
	max(coalesce(ie_tipo_fonte_prescr,' '))
into STRICT	ds_material_w,
	ie_tipo_fonte_w
from 	material
where	cd_material = cd_material_w;

ds_local_w			:= '';
/* obter a localização do material */

if (ie_localizacao_p = 'S') and (coalesce(cd_local_w,0) > 0) then
	ds_local_w	:= substr(obter_localizacao_material(cd_material_w, cd_local_w),1,80);

	if (ds_local_w IS NOT NULL AND ds_local_w::text <> '') then
		ds_local_w	:= ' [' || ds_local_w || ']';
	end if;
end if;

ds_retorno_w		:= ds_material_w || ds_local_w;

/* caracteres de formatação da string*/

if (ie_urgencia_w = 'S') then
	ds_retorno_w	:= '#' || ds_retorno_w || '#' || 'NI' ||'#P';
elsif (ie_tipo_fonte_w <> ' ') then
	if (ie_tipo_fonte_w = 'M') then
		ds_retorno_w 	:= upper(ds_retorno_w);
	elsif (ie_tipo_fonte_w = 'MS') then
		begin
		ds_retorno_w 	:= upper(ds_retorno_w);
		ie_tipo_fonte_w	:= 'S';
		ds_retorno_w	:= '#' || ds_retorno_w || '#' || ie_tipo_fonte_w ||'#P';
		end;
	else
		ds_retorno_w	:= '#' || ds_retorno_w || '#' || ie_tipo_fonte_w ||'#P';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
