CREATE OR REPLACE FUNCTION obter_desc_mat_reduzida_req ( nr_requisicao_p bigint, nr_sequencia_p bigint, ie_localizacao_p text, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(128);
ds_material_w		varchar(100);
ie_tipo_fonte_w		varchar(003);
ds_local_w		varchar(80);
cd_material_w		integer;
ie_urgencia_w		varchar(01);
cd_motivo_baixa_w	smallint;


BEGIN

select	cd_material
into STRICT	cd_material_w
from	item_requisicao_material
where	nr_requisicao		= nr_requisicao_p
  and	nr_sequencia		= nr_sequencia_p;

select	ie_urgente
into STRICT	ie_urgencia_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p;


select	substr(max(ds_reduzida),1,100),
	max(coalesce(ie_tipo_fonte_prescr,' '))
into STRICT	ds_material_w,
	ie_tipo_fonte_w
from 	material
where	cd_material = cd_material_w;

if (ie_localizacao_p = 'S') then
	Select ' [' || substr(obter_localizacao_material(cd_material_w, cd_local_estoque_p),1,80) || ']'
	into STRICT	ds_local_w
	;
	ds_retorno_w		:= ds_material_w || ds_local_w;
else
	ds_retorno_w	:= ds_material_w;
end if;

if (ie_urgencia_w = 'S') then
	ds_retorno_w	:= '#' || ds_retorno_w || '#' || 'NI' ||'#P';
elsif (ie_tipo_fonte_w = 'MS') then
	begin
	ds_retorno_w 	:= upper(ds_retorno_w);
	ie_tipo_fonte_w	:= 'S';
	ds_retorno_w	:= '#' || ds_retorno_w || '#' || ie_tipo_fonte_w ||'#P';
	end;
elsif (ie_tipo_fonte_w <> ' ') and (ie_tipo_fonte_w <> 'M') then
	ds_retorno_w	:= '#' || ds_retorno_w || '#' || ie_tipo_fonte_w ||'#P';
elsif (ie_tipo_fonte_w = 'M') then
	ds_retorno_w	:= upper(ds_retorno_w);
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
