CREATE OR REPLACE FUNCTION obter_desc_mat_seq_prescr ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/*ie_tipo_p
D  = Descrição do item
DO = Descrição do item de origem
*/
cd_material_w		integer;
nr_seq_origem_w		bigint;
ds_retorno_w		varchar(255);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then
	begin

	if (ie_tipo_p = 'D') then
		select	coalesce(max(cd_material), 0)
		into STRICT	cd_material_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_prescr_p;
	elsif (ie_tipo_p = 'DO') then

		select	coalesce(coalesce(nr_sequencia_diluicao, nr_seq_kit), nr_seq_prescr_p)
		into STRICT	nr_seq_origem_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_prescr_p;

		select	coalesce(max(cd_material), 0)
		into STRICT	cd_material_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_origem_w;
	end if;

	if (cd_material_w > 0) then
		select	ds_material
		into STRICT	ds_retorno_w
		from	material
		where	cd_material = cd_material_w;
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
