CREATE OR REPLACE FUNCTION obter_desc_mat_tipo_fonte ( cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ie_tipo_fonte_prescr_w  varchar(3);

-------> Tratado apenas as opções de fonte maiuscula, caso necessário realizar os outros trtamentos <-----------
BEGIN
begin

if (coalesce(cd_material_p,0) > 0) then

	SELECT	substr(max(ds_material),1,255),
		MAX(coalesce(ie_tipo_fonte_prescr,' '))
	INTO STRICT	ds_retorno_w,
		ie_tipo_fonte_prescr_w
	FROM 	material
	where 	cd_material = cd_material_p;

	if 	((ie_tipo_fonte_prescr_w = 'M') or (ie_tipo_fonte_prescr_w = 'MI') or (ie_tipo_fonte_prescr_w = 'MN') or (ie_tipo_fonte_prescr_w = 'MS') or (ie_tipo_fonte_prescr_w = 'MT')) then
		 ds_retorno_w	:= substr(UPPER(ds_retorno_w),1,255);
	end if;

end if;

RETURN ds_retorno_w;

exception
when others then
	null;
end;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
