CREATE OR REPLACE FUNCTION obter_desc_medico_convenio ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE



ds_convenio_w		varchar(255);
ds_convenio_ww		varchar(255);

c01 CURSOR FOR
SELECT	distinct
	b.ds_convenio
from	convenio b,
	medico_convenio a
where	a.cd_convenio	= b.cd_convenio
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
order by	b.ds_convenio;

BEGIN


open C01;
loop
fetch C01 into
	ds_convenio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_convenio_ww	:= substr(ds_convenio_ww || ds_convenio_w || ', ',1,255);
	end;
end loop;
close C01;

if (substr(ds_convenio_ww,length(ds_convenio_ww)-1,2) = ', ') then
	ds_convenio_ww		:= substr(ds_convenio_ww,1,length(ds_convenio_ww)-2);
end if;

return	ds_convenio_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
