CREATE OR REPLACE FUNCTION obter_desc_medic_gpt ( cd_material_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(700);
ie_item_proc_w		bigint;
ie_item_hemo_w		bigint;
ie_dose_adicional_w	varchar(1);
ie_dose_ataque_w	varchar(1);


BEGIN 
 
ds_retorno_w := obter_desc_material(cd_material_p);
 
select	coalesce(nr_seq_procedimento,0), 
	coalesce(nr_seq_hemoterapia,0), 
	ie_dose_adicional, 
	ie_dose_ataque 
into STRICT	ie_item_proc_w, 
	ie_item_hemo_w, 
	ie_dose_adicional_w, 
	ie_dose_ataque_w 
from	cpoe_material 
where	nr_sequencia = nr_sequencia_p;
 
	if (ie_item_proc_w <> 0) then 
		ds_retorno_w := ds_retorno_w || ' (' || obter_desc_expressao_idioma(339771,null,wheb_usuario_pck.get_nr_seq_idioma) || ')';
	elsif (ie_item_hemo_w <> 0) then 
		ds_retorno_w := ds_retorno_w || ' (' || obter_desc_expressao_idioma(291262,null,wheb_usuario_pck.get_nr_seq_idioma) || ')';
	elsif (ie_dose_adicional_w = 'S') then 
		ds_retorno_w := ds_retorno_w || ' (' || obter_desc_expressao_idioma(656390,null,wheb_usuario_pck.get_nr_seq_idioma) || ')';
	elsif (ie_dose_ataque_w = 'S') then 
		ds_retorno_w := ds_retorno_w || ' (' || obter_desc_expressao_idioma(303811,null,wheb_usuario_pck.get_nr_seq_idioma) || ')';
	end if;
 
return	ds_retorno_w;
 
end;
$body$
