CREATE OR REPLACE FUNCTION obter_desc_med_especialidade ( cd_especialidade_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);			
	 

BEGIN 
 
if (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') then 
	 
	select	max(substr(obter_descricao_padrao('ESPECIALIDADE_MEDICA','DS_ESPECIALIDADE',CD_ESPECIALIDADE),1,50)) 
	into STRICT	ds_retorno_w 
	from	medico_especialidade 
	where	cd_especialidade = cd_especialidade_p;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
