SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_med_justificar ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_mat_justificativa_w	varchar(1000);


BEGIN

begin
select	substr(obter_desc_material(cd_material),1,40) || '  ' || Wheb_mensagem_pck.get_texto(309034) || ': ' /*'  Consumo: '*/ ||
	sum(coalesce(qt_dose_consumo,0)) || ' ' || cd_unidade_consumo || '  ' || Wheb_mensagem_pck.get_texto(309035) || ': ' /*'  Dispensado: '*/ ||
	sum(coalesce(qt_dispensacao,0)) || ' ' || cd_unidade_dispensacao
into STRICT	ds_mat_justificativa_w
from	w_resumo_agente
where	nr_sequencia = nr_sequencia_p
group by substr(obter_desc_material(cd_material),1,40),
	cd_unidade_consumo,
	cd_unidade_dispensacao;
exception
	when others then
	ds_mat_justificativa_w	:= Wheb_mensagem_pck.get_texto(309100); --'Justificativa';
end;

return ds_mat_justificativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
