SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_med_mistura_quimio (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_medicamento_w	varchar(100) := '';

c01 CURSOR FOR
	SELECT	substr(obter_desc_material(a.cd_material),1,60) || a.cd_unid_med_prescr || ' ' || a.qt_dose_prescricao
	from	material b,
			paciente_atend_medic a
	where 	a.cd_material = b.cd_material
	and		nr_seq_atendimento = nr_seq_atendimento_p
	and		b.ie_mistura = 'S';


BEGIN
if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		ds_medicamento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := ds_medicamento_w;
		else
			ds_retorno_w := ds_retorno_w || CHR(10) || ds_medicamento_w;
		end if;
		end;
	end loop;
	close c01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
