CREATE OR REPLACE FUNCTION obter_desc_med_rel_fat ( ie_proc_mat_p bigint, ie_tipo_atendimento_p bigint, ie_nome_medico_p text, ie_imprime_executor_p text, cd_medico_resp_p text, cd_medico_executor_p text) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w		varchar(255) := '';
cd_medico_w		varchar(255);			
 

BEGIN 
 
if (ie_proc_mat_p IS NOT NULL AND ie_proc_mat_p::text <> '') and (ie_tipo_atendimento_p IS NOT NULL AND ie_tipo_atendimento_p::text <> '') and (ie_nome_medico_p IS NOT NULL AND ie_nome_medico_p::text <> '') and (ie_imprime_executor_p IS NOT NULL AND ie_imprime_executor_p::text <> '') then 
	 
	if (ie_proc_mat_p = 1) then 
	 
		if (ie_tipo_atendimento_p = 7) and (ie_imprime_executor_p = 'N') then 
			 
			cd_medico_w := cd_medico_resp_p;
			 
		else 
 
			cd_medico_w := cd_medico_executor_p;
			 
		end if;
		 
		if (ie_nome_medico_p = 'S') then 
 
			select 	substr(obter_nome_pf(cd_medico_w),1,100) nm_medico 
			into STRICT	ds_retorno_w 
			;
			 
		else	 
 
			select 	ds_doc_medico 
			into STRICT	ds_retorno_w	 
			from 	medico_v 
            		where 	cd_pessoa_fisica = cd_medico_w;
		 
		end if;	
	 
	end if;
 
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
