CREATE OR REPLACE FUNCTION obter_desc_mes_ano ( dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w			varchar(30);



BEGIN

if (ie_opcao_p = 'N') then
	ds_retorno_w		:= to_char(dt_referencia_p,'mm/yyyy');
elsif (ie_opcao_p = 'A') then
	ds_retorno_w		:= replace(to_char(dt_referencia_p,'month/yyyy'),' ','');
elsif (ie_opcao_p = 'ANO') then
	ds_retorno_w		:= replace(to_char(dt_referencia_p,'yyyy'),' ','');
elsif (ie_opcao_p = 'AB') then
	ds_retorno_w		:= substr(to_char(dt_referencia_p,'month'),1,3) || to_char(dt_referencia_p,'/yyyy');
elsif (ie_opcao_p = 'ABM') then
	ds_retorno_w		:= upper(substr(to_char(dt_referencia_p,'month'),1,3) || to_char(dt_referencia_p,'/yyyy'));
elsif (ie_opcao_p = 'ABM2') then
	ds_retorno_w		:= upper(replace(to_char(dt_referencia_p,'month'),' ', '') || to_char(dt_referencia_p,'/yyyy'));
end if;

Return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
