SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_microorg_grupo (cd_microorganismo_cih_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_grupo_w	bigint;
ds_grupo_w	varchar(40);
ie_grupo_microorg_w varchar(1);


BEGIN

select	coalesce(max(IE_GRUPO_MICROORG),'N')
into STRICT	ie_grupo_microorg_w
from	lab_parametro;

Select	max(ds_microorganismo), max(nr_seq_grupo)
into STRICT	ds_retorno_w, nr_seq_grupo_w
from	cih_microorganismo
where	cd_microorganismo = cd_microorganismo_cih_p;

if (ie_grupo_microorg_w = 'S') then

	select	max(ds_grupo)
	into STRICT	ds_grupo_w
	from	cih_grupo_microorg
	where	nr_sequencia = nr_seq_grupo_w;

	if (ds_grupo_w IS NOT NULL AND ds_grupo_w::text <> '') then
		ds_retorno_w := ds_retorno_w || ' ('|| ds_grupo_w || ')';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
